    <div>
      <div className="container" style={{ margin: "30px" }}>
        <div className="row">
          <div className="col-3">
            <div class="card" >
              {/* <img src={profilepic} class="card-img-top" alt="alt" height={"200px"} width={"100px"}/> */}
              <div class="card-body">
                <h5 class="card-title"> {profile.name} <hr/>{profile.gender}, {profile.age} years old</h5>
