import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, redirect, useNavigate } from "react-router-dom";

function Adminpage() {
  const [cusdata, setcdata] = useState([
    { name: "cus1", email: "testmail1", empid: 1, gender: "male" },
    { name: "cus2", email: "testmail2", empid: 2, gender: "female" },
  ]);
  const [docdata, setddata] = useState([
    { name: "doc1", email: "testmail1", empid: 1, gender: "male" },
    { name: "doc2", email: "testmail2", empid: 2, gender: "female" },
  ]);

  useEffect(() => {
    axios
      .post(`http://43.204.92.123:4002/health_empire_api/viewCustomers`)
      .then((res) => {
        console.log(res);
        setcdata(res.data);
      })
      .catch((err) => {
        console.log(err);
      });

    axios
      .post(`http://43.204.92.123:4002/health_empire_api/viewAllDoctors`)
      .then((res) => {
        console.log(res);
        setddata(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  if (localStorage.getItem("adminlog") == 1) {
    return (
      <div className="productdiv1" style={{ minHeight: "400px" }}>
        <button
          className="btn btn-dark"
          onClick={() => {
            localStorage.removeItem("adminlog");
            alert("Logged out");
            window.location.reload(false);
          }}
        >
          Logout
        </button>
        <div className="main">
          <h2 style={{ textAlign: "center" }}>
            Hi Admin, Welcome to the Admin Panel
          </h2>
          <div class="container text-center">
            <div class="row">
              <div class="col">
                <button
                  class="btn btn-primary"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#multiCollapseExample1"
                  aria-expanded="false"
                  aria-controls="multiCollapseExample1"
                >
                  View all Customers
                </button>
              </div>
              <div class="col">
                <button
                  class="btn btn-primary"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target="#multiCollapseExample2"
                  aria-expanded="false"
                  aria-controls="multiCollapseExample2"
                >
                  View all Doctors
                </button>
              </div>
            </div>
          </div>

          <div class="container text-center">
            <div class="row">
              <div class="col">
                <div class="collapse multi-collapse" id="multiCollapseExample1">
                  {/* stddata details mapped */}
                  {cusdata.map((a) => {
                    return (
                      <div class="card card-body">
                        <div className="main">
                          <div class="card" style={{ width: "18rem;" }}>
                            <div class="card-body">
                              <h5 class="card-title">
                                Customer name : {a.name}
                              </h5>
                              <p class="card-text">Email : {a.email}</p>
                              <p class="card-text">Gender : {a.gender}</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                  {/* journalist details mapped */}
                </div>
              </div>
              <div class="col">
                <div class="collapse multi-collapse" id="multiCollapseExample2">
                  {/* stddata details mapped */}
                  {docdata.map((a) => {
                    return (
                      <div class="card card-body">
                        <div className="main">
                          <div class="card" style={{ width: "18rem;" }}>
                            <div class="card-body">
                              <h5 class="card-title">Doctor name : {a.name}</h5>
                              <p class="card-text">Email : {a.email}</p>
                              <p class="card-text">Gender : {a.gender}</p>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                  {/* journalist details mapped */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div style={{ minHeight: "400px" }}>
        <h1 style={{ textAlign: "center", position: "relative", top: "150px" }}>
          Please{" "}
          <Link style={{ fontSize: "50px" }} to="/Admin">
            log in{" "}
          </Link>
          to see admin panel{" "}
        </h1>
      </div>
    );
  }
}

export default Adminpage;
