import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axiosInstance from "../../baseurl";

function ViewProgramTutorials() {
  const { id } = useParams();

  const [approvelist, setapprlist] = useState([]);
  const [approvedlist, setapprovedlist] = useState([]);
  useEffect(() => {
    axiosInstance
      .post(`/viewTutorialRequestsByPgmId/${id}`)
      .then((res) => {
        console.log(res, " Tutorial request list");
        if(res.data.data!=undefined){
            setapprlist(res.data.data)
        }
        else{
            setapprlist([])
        }
      })
      .catch((err) => {
        console.log(err, "error tutorial req list");
      });

    axiosInstance
      .post(`/viewtutorialByPgmId/${id}`)
      .then((res) => {
        console.log(res, "approved tutorials");
        if(res.data.data!=undefined){
            setapprovedlist(res.data.data)
        }
        else{
            setapprovedlist([])
        }
    })
      .catch((err) => {
        console.log(err, "approved tutorial error");
      });
  }, []);

  const approvefn = ()=>{
    axiosInstance.post(`/`)
  }
  return (
    <div>
      <div class="accordion" id="accordionExample">
        <div class="accordion-item">
          <h2 class="accordion-header">
            <button
              class="accordion-button"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseOne"
              aria-expanded="true"
              aria-controls="collapseOne"
            >
              View Approved Tutorials
            </button>
          </h2>
          <div
            id="collapseOne"
            class="accordion-collapse collapse show"
            data-bs-parent="#accordionExample"
          >
            <div class="accordion-body">
              <div className="container text-center">
                <div className="row">
                  {approvedlist.length
                    ? approvedlist.map((a) => {
                        return (
                          <div className="col">
                            <div class="card" style={{ width: "18rem" }}>
                              <div class="card-body">
                                <h5 class="card-title">{a.title}</h5>
                                <p class="card-text">
                                  {a.category}, {a.description}
                                </p>
                              
                              </div>
                            </div>
                          </div>
                        );
                      })
                    : null}
                </div>
              </div>
            </div>
          </div>
        </div>
        <div class="accordion-item">
          <h2 class="accordion-header">
            <button
              class="accordion-button collapsed"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseTwo"
              aria-expanded="false"
              aria-controls="collapseTwo"
            >
              Approve Tutorials
            </button>
          </h2>
          <div
            id="collapseTwo"
            class="accordion-collapse collapse"
            data-bs-parent="#accordionExample"
          >
            <div class="accordion-body">
              <div className="container text-center">
                <div className="row">
                  {approvelist.length
                    ? approvelist.map((a) => {
                        return (
                          <div className="col">
                            <div class="card" style={{ width: "18rem" }}>
                              <div class="card-body">
                                <h5 class="card-title">{a.title}</h5>
                                <p class="card-text">
                                  {a.category}, {a.description}
                                </p>
                                <button
                                  className="btn btn-primary"
                                  onClick={()=>{approvefn(a._id)}}
                                >
                                  Approve tutorial
                                </button>
                              </div>
                            </div>
                          </div>
                        );
                      })
                    : null}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ViewProgramTutorials;
