import React, { useEffect, useState } from "react";
import axiosInstance from "../baseurl";


function ViewAllTrainers() {
  const [trainerdata, setdata] = useState([
    { name: "trainer1", email: "testmail1", empid: 1, gender: "male" },
    { name: "trainer2", email: "testmail2", empid: 2, gender: "female" },
  ]);

  useEffect(() => {
    axiosInstance
      .post(`/viewtrainers`)
      .then((res) => {
        console.log(res, "trainers");
        if (res.data.msg == "No Data obtained ") {
          setdata([]);
        } else {
          setdata(res.data.data);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  return (
    <div>
        <div class="accordion" id="accordionExample">
  <div class="accordion-item">
    <h2 class="accordion-header">
      <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
        Accordion Item #1
      </button>
    </h2>
    <div id="collapseOne" class="accordion-collapse collapse show" data-bs-parent="#accordionExample">
      <div class="accordion-body">
      <div className="container">
        <div class="container text-center">
          <div class="row">
            {/* trainerdata details mapped */}
            {trainerdata.length ? (
              trainerdata.map((a) => {
                return (
                  <div className="col">
                    <div class="card card-body">
                      <div className="main">
                        <div class="card" style={{ height: "170px" }}>
                          <div class="card-body">
                            <h5 class="card-title">{a.name}</h5>
                            <p class="card-text">Email : {a.email}</p>
                            <p class="card-text">Gender : {a.gender}</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })
            ) : (
              <div class="card card-body">
                <div className="main">
                  <div class="card" style={{ width: "18rem;" }}>
                    <div class="card-body">
                      <h5 class="card-title">No data</h5>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>      </div>
    </div>
  </div>
 
</div>
      
    </div>
  );
}

export default ViewAllTrainers;
