import axios from "axios";
import React, { useEffect, useState } from "react";
import axiosInstance from "../../baseurl";

function Customerviewprograms() {
  const [allprograms, setallprograms] = useState([]);

  useEffect(() => {
    axiosInstance
      .post(`/viewPgms`)
      .then((res) => {
        console.log(res);
        setallprograms(res.data.data)
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const subscribe = (id)=>{
    axiosInstance.post(`/subscribePgm`, {pid:id, cid:localStorage.getItem()})
  }
  return (
    <div style={{ minHeight: "400px", margin: "50px" }}>
      <div class="accordion" id="accordionExample">
        <div class="accordion-item">
          <h2 class="accordion-header">
            <button
              class="accordion-button"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#collapseOne"
              aria-expanded="true"
              aria-controls="collapseOne"
            >
              View All Programs
            </button>
          </h2>
          <div
            id="collapseOne"
            class="accordion-collapse collapse show"
            data-bs-parent="#accordionExample"
          >
            <div class="accordion-body">
              <div class="container text-center">
                <div class="row">
                  {allprograms.length
                    ? allprograms.map((a) => {
                        return (
                          <div class="col">
                            <div class="card" style={{ width: "18rem" }}>
                             
                              <div class="card-body">
                                <h2 class="card-title">{a.title}</h2>
                                <h6>{a.category}</h6>
                                <p class="card-text">
                                 {a.description}
                                </p>
                                <button onClick={()=>{subscribe(a._id)}} class="btn btn-primary">
                                 Subscribe
                                </button>
                              </div>
                            </div>
                          </div>
                        );
                      })
                    : 
                          <div class="col-12">
                            <div class="card" >
                              <div class="card-body">
                                <h5 class="card-title">
                                  No Programs available at the moment
                                </h5>
                              </div>
                            </div>
                          </div>
                        
                      }
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Customerviewprograms;
