import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import axiosInstance from "../../baseurl";

function ViewTutorialFull() {
  const { id } = useParams();
  const [videodata,setvdata] = useState({video:{filename:''}})

  useEffect(()=>{
    console.log(id);
    axiosInstance.post(`/viewtutorialById/${id}`)
    .then((res)=>{
      console.log(res,'video');
      setvdata(res.data.data)
    })
    .catch((err)=>{console.log(err);})
  },[])
  return <div>
    <Link className="btn btn-primary" to={`/Customer/CustomerViewTutorials/${id}`}> Back to Tutorials</Link>
    <video width='100%' height='200' controls>
      <source  src={`http://localhost:4002/${videodata.video.originalname}`}/>
    </video>
  </div>;
}

export default ViewTutorialFull;
