import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../../baseurl";

function Chat() {
  const { id } = useParams();
  const [msg,setmsg] = useState('')

  useEffect(() => {
    axiosInstance
      .post(`/viewChatForDrwithCust/${id}`, {
        cid: localStorage.getItem("CustomerLogId"),
      })
      .then((res) => {
        console.log(res, "view chat");
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const submitfn = ()=>{
    axiosInstance.post(`/`)
  }
  return (
    <div>
      <div className="chatdiv "  style={{ minHeight: "400px", padding:"100px", width:"50%", margin:"auto"}}>
        <div style={{border:"2px solid black", borderRadius:"10px", minHeight:"300px", padding:"50px", margin:"10px"}}>
            Doctor: Hi. How can i help you?
        </div>
        <form onSubmit={submitfn}>
        <div class="input-group mb-3">
          <input type="text" class="form-control"  aria-describedby="basic-addon2" onChange={(e)=>{setmsg(e.target.value)}}/>
          <span class="input-group-text" id="basic-addon2"><button> Send</button></span>
        </div>
        </form>
      </div>
    </div>
  );
}

export default Chat;
