import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axiosInstance from "../../baseurl";

function Chat() {
  const { id } = useParams();
  const [msg, setmsg] = useState("");
  const [chat, setchat] = useState([]);

  useEffect(() => {
    axiosInstance
      .post(`/viewChatForDrwithCust`, {
        cid: localStorage.getItem("CustomerLogId"),
        drid: id,
      })
      .then((res) => {
        console.log(res, "view chat");
        if (res.data.data != undefined) {
          setchat(res.data.data);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const submitfn = (e) => {
    e.preventDefault();
    axiosInstance
      .post(`/createChat`, {
        cid: localStorage.getItem("CustomerLogId"),
        drid: id,
        msg: msg,
        from: "customer",
      })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <div
        className="chatdiv "
        style={{
          minHeight: "400px",
          padding: "100px",
          width: "50%",
          margin: "auto",
        }}
      >
        <div
          style={{
            border: "2px solid black",
            borderRadius: "10px",
            minHeight: "300px",
            padding: "50px",
            margin: "10px",
          }}
        >
          
          {chat.length
            ? chat.map((a) => {
                return <div>{a.msg}</div>;
              })
            : <p>Doctor: Hi. How can i help you?</p>}
        </div>
        <form onSubmit={submitfn}>
          <div class="input-group mb-3">
            <input
              type="text"
              class="form-control"
              aria-describedby="basic-addon2"
              onChange={(e) => {
                setmsg(e.target.value);
              }}
            />
            <span class="input-group-text" id="basic-addon2">
              <button> Send</button>
            </span>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Chat;
