import React, { useEffect, useState } from "react";
import axiosInstance from "../baseurl";
import { useParams } from "react-router-dom";

function ViewGymByID() {
  const { id } = useParams();
  const [profile, setprofile] = useState({ img: [{ originalname: "" }] });
  const [msg,setmsg] = useState('')
  useEffect(() => {
    axiosInstance
      .post(`/viewGymById/${id}`)
      .then((res) => {
        console.log(res, "result");
        setprofile(res.data.data);
      })
      .catch((err) => {
        console.log(err, "error");
      });
  }, []);
  const bookGym = (gymid) => {
    axiosInstance
      .post(`/bookGym`, {
        cid: localStorage.getItem(`CustomerLogId`),
        gymid: gymid,
        message: msg
      })
      .then((res) => {
        console.log(res);
        if(res.data.status==200){
          alert("Booked this gym")
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div className="productdiv1 container-top" style={{ minHeight: "700px" }}>
      <div>
        <h2 style={{ textAlign: "center" }}>Welcome to {profile.name}.</h2>
        <div class="card card-body">
          <div className="container">
            <div className="row">
              <div className="col-12">
                <div class="card">
                  <div class="card-body" style={{ textAlign: "center" }}>
                    <h3 class="card-text">Email : {profile.email} </h3>
                    <h3 class="card-text">Contact : {profile.contact} </h3>
                  </div>
                </div>
              </div>
              <div id="carouselExample" class="carousel slide">
                <div class="carousel-inner">
                  {profile.img.map((a) => {
                    if (a.filename != undefined) {
                      console.log(a.filename);
                      return (
                        <div class="carousel-item active">
                          <img
                            src={`http://localhost:4002/${a.originalname}`}
                            class="d-block w-100"
                            alt="..."
                            height={700}
                          />
                        </div>
                      );
                    }
                  })}
                </div>
                <button
                  class="carousel-control-prev"
                  type="button"
                  data-bs-target="#carouselExample"
                  data-bs-slide="prev"
                >
                  <span
                    class="carousel-control-prev-icon"
                    aria-hidden="true"
                  ></span>
                  <span class="visually-hidden">Previous</span>
                </button>
                <button
                  class="carousel-control-next"
                  type="button"
                  data-bs-target="#carouselExample"
                  data-bs-slide="next"
                >
                  <span
                    class="carousel-control-next-icon"
                    aria-hidden="true"
                  ></span>
                  <span class="visually-hidden">Next</span>
                </button>
              </div>
            </div>
          </div>
        </div>
        <br />
        
        <p style={{ textAlign: "center" }}>
        <input type="text" className="btn btn-primary" onChange={(e)=>{setmsg(e.target.value)}}/>
          <button
            class="btn btn-primary"
            onClick={()=>{bookGym(profile._id)}}
          >
           Book Gym
          </button>
        </p>
      </div>
    </div>
  );
}

export default ViewGymByID;
