import React, { useEffect, useState } from "react";
import axiosInstance from "../../baseurl";

function Nutrition() {
  const [profile, setprofile] = useState({});
  const [BMI, setBMI] = useState((profile.weight / ((profile.height / 100) * 2)).toFixed(2))
  useEffect(() => {
    axiosInstance
      .post(`/viewCustomerById/${localStorage.getItem("CustomerLogId")}`)
      .then((res) => {
        console.log(res, "result");
        setprofile(res.data.data);
        
      })
      .catch((err) => {
        console.log(err, "error");
      });
  }, []);
  return (
    <div>
      <div className="container" style={{ margin: "30px" }}>
        <div className="row">
          <div className="col-3">
            <div class="card" >
              {/* <img src={profilepic} class="card-img-top" alt="alt" height={"200px"} width={"100px"}/> */}
              <div class="card-body">
                <h5 class="card-title"> {profile.name} <hr/>{profile.gender}, {profile.age} years old</h5>
                <p class="card-text"> </p>
                <p class="card-text">height : {profile.height}cm </p>
                <p class="card-text">weight : {profile.weight}kg </p>
                <h1>
                 {BMI}
                </h1>
                
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Nutrition;
