import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, redirect, useNavigate } from "react-router-dom";
import axiosInstance from "../../baseurl";

function Adminpage() {

  const [doc,setdoc] = useState([])
  const [trainer,settrainer] = useState([])
  const [cust,setcust] = useState([])
  const [gym,setgym] = useState([])

  useEffect(()=>{
    axiosInstance.post(`/viewCustomers`)
    .then((res)=>{console.log(res,' all cust')
  if(res.data.data!=undefined){
    setcust(res.data.data)
  }
  })
    .catch((err)=>{console.log(err);})

    axiosInstance.post(`/viewAllDoctors`)
    .then((res)=>{console.log(res,' all Docs')
    if(res.data.data!=undefined){
      setdoc(res.data.data)
    }})
    .catch((err)=>{console.log(err);})

    axiosInstance.post(`/viewGyms`)
    .then((res)=>{console.log(res,' all Gyms')
    if(res.data.data!=undefined){
      setgym(res.data.data)
    }
  })
    .catch((err)=>{console.log(err);})


    axiosInstance.post(`/viewtrainers`)
    .then((res)=>{console.log(res,' all Trainers')
    if(res.data.data!=undefined){
      settrainer(res.data.data)
    }})
    .catch((err)=>{console.log(err);})
    
  },[])


  if (localStorage.getItem("adminlog") == 1) {
    return (
      <div class="container-top" style={{ minHeight: "400px" }}>
       <h1> Hi Admin. What would you like to do?</h1>
       <div class="alert alert-primary" role="alert">
       {doc.length} Doctors available
</div>
       <p> {doc.length} DOC</p>
       <p> {trainer.length} Trainer</p>
       <p> {cust.length} Cust</p>
       <p> {gym.length} Gym</p>



      </div>
    );
  } else {
    return (
      <div style={{ minHeight: "400px" }} class="container-top">
        <h1 style={{ textAlign: "center", position: "relative", top: "150px" }}>
          Please{" "}
          <Link style={{ fontSize: "50px" }} to="/Admin">
            log in{" "}
          </Link>
          to see admin panel{" "}
        </h1>
      </div>
    );
  }
}

export default Adminpage;
